targets.query:
from(bucket: "$INFLUX_BUCKET")
  |> range(start: -6h)
  |> filter(fn: (r) => r._measurement == "blockchain" and r._field =~ /best_height|headers_height/)

from(bucket: "$INFLUX_BUCKET")
  |> range(start: -6h)
  |> filter(fn: (r) => r._measurement == "process" and r._field =~ /cpu_percent|memory_rss_mb/)
