targets.query:
from(bucket: "$INFLUX_BUCKET")
  |> range(start: -12h)
  |> filter(fn: (r) => r._measurement == "blockchain" and r._field == "verification_progress")

from(bucket: "$INFLUX_BUCKET")
  |> range(start: -12h)
  |> filter(fn: (r) => r._measurement == "blockchain" and r._field == "block_interval_seconds")

from(bucket: "$INFLUX_BUCKET")
  |> range(start: -12h)
  |> filter(fn: (r) => r._measurement == "zmq" and r._field == "rawblock_per_min")

from(bucket: "$INFLUX_BUCKET")
  |> range(start: -30d)
  |> filter(fn: (r) => r._measurement == "blockchain" and r._field == "max_reorg_depth")
